import React, { useState } from 'react';
import { Link } from "react-router-dom";
import "./Login.css";

function Login() {
  const [emessage,setemessage] = useState("");
    function Signup(){
         const username = document.getElementById('username').value;
         const password = document.getElementById('password').value;
         if(username===""||password===""){
            setemessage("All fields are required");
         }else{
            
            alert("Account Created Sucessfully! login now");
                    
                
         }
        }
  return (
    <div className='LoginSection'>
            <div  className={emessage===""?"hide":"EMessage"}><p>{emessage}</p></div>
      <div className="box-form">
        <div className="left">
          <div className="overlay">
              <h1>Hello.</h1>
              <p>Welcome to GiveItToUs. Your portal to give away to the poor or give us something to sell for you. Login or Signup to Begin</p>
              <nav>
                <ul>
                  <a href="/gmail"><i className="sin " aria-hidden="true" /> Login with G-Mail</a>
                  <Link to="/admin"><i className="sin " aria-hidden="true" /> Admin Login </Link>
                  <Link to="/seller"><i className="sin" aria-hidden="true" /> Seller Login </Link>
                </ul>
              </nav>
          </div>
        </div>
          <div className="right">
            <h5>Login</h5>
            <p>Don't have an account? <Link to="/register"><i className="sin " aria-hidden="true" /> Register now </Link>it takes less than a minute</p>
            <div className="inputs">
              <input type="text" id="username" placeholder="user name" />
              <br />
              <input type="password" id="password"placeholder="password" />
            </div>
            <br /><br />
            <div className="remember-me--forget-password">
              <label>
                <input type="checkbox" name="item" defaultChecked />
                <span className="text-checkbox">Remember me</span>
              </label>
              <p>forgot password?</p>
            </div>
            <br />
            <button className="login">Login</button>
          </div>
        </div> 
        
      </div>
  );
}

export default Login;
